import i18n from "../../i18n";
import axios from 'axios';
import { getField, updateField } from 'vuex-map-fields'; 
const state = {
  translations:[],
  formContract:[],
  ViewOperations:[],
   //login
  token: localStorage.getItem('accessToken') || null, 
  loginuser:JSON.parse(localStorage.getItem('userinfo')) || null,
  //filters
   position:[],
   roles:[],
   userRoles:[],
   orgs:[],
  //main
  newagreement: [],
  historyCont:[],
  oneCont: [],
  search:"",
   
   //workers   
    workers:[],
   
    //users
    usersdata:[],
    recivers:[],
    newuser:{
      roleid:'',
    }, 
    secretUserlg:'',
    secretUserpass:'',

    user_tasks:[],
    //organizations
    mainOrgs:[],
    //dictionary
    dictionary:[],
    contractType:[],
    contractLiability:[],
    town:[],
    country:[],
    region:[],
     tasktype:[],
    dicByCode:[],
    //sellers
    sellers:[],
    //feetarifs
    feeTarifs:[],
    //payment
    payments:[],
    payplan:[],
    files:[],
    tasksCount:null,
    closeOnContentClick:false,
    orgUnitType:[],
    suborgs:[],
    view_components:[],
    contractProcessState:[],
    searchedContract:[]
};

const getters = {
  closeOnContentClick:state => state.closeOnContentClick,
  getField, 
  alltranslations:state => state.translations,
  loggedIn(state) { return state.token !== null },
  search: (state) => {  return state.search},
  getuserRoles: (state) => {  return state.userRoles},
  loginuser: (state) => {  return state.loginuser},
  allnewagreement: state => state.newagreement, 
  formData: state => state.oneCont,
  historyContract:state => state.historyCont,
  allformContract:state => state.formContract,
  filteredagreements: function(state, getters){ 
    return state.newagreement.filter((contract)=>{
       return contract.createdByPersonnelName.toLowerCase().match(getters.search.toLowerCase())||
       contract.createdByPersonnelPatronymic.toLowerCase().match(getters.search.toLowerCase())|| 
       contract.createdByPersonnelSurname.toLowerCase().match(getters.search.toLowerCase())|| 
       contract.sellerPersonnelName.toString().match(getters.search.toLowerCase())|| 
       contract.sellerPersonnelPatronymic.toLowerCase().match(getters.search.toLowerCase())|| 
       contract.sellerPersonnelSurname.toLowerCase().match(getters.search.toLowerCase())|| 
       contract.contractNumber.toLowerCase().match(getters.search.toLowerCase())|| 
       contract.contractDate.toString().toLowerCase().match(getters.search.toLowerCase())|| 
       contract.contractTypeDescription.toLowerCase().match(getters.search.toLowerCase())
     })
  },
 
  //workers
  filteredworkers: function(state, getters){ 
    return state.workers.filter((worker)=>{ 
       return worker.name.toLowerCase().match(getters.search.toString().toLowerCase()) || 
       worker.surname.toLowerCase().match(getters.search.toString().toLowerCase()) ||  
       worker.birthDate.toString().match(getters.search.toString().toLowerCase())|| 
       worker.entranceDate.toString().match(getters.search.toString().toLowerCase())  
     })
  },
  allworkers: state => state.workers, 
 
  //users
  filteredusers: function(state, getters){  
    return state.usersdata.filter((userdata)=>{
       return userdata.loginId.toLowerCase().match(getters.search.toLowerCase()) ||
        userdata.personnelSurname.toLowerCase().match(getters.search.toLowerCase()) ||
        userdata.personnelName.toLowerCase().match(getters.search.toLowerCase())  ||
        userdata.personnelPatronymic.toLowerCase().match(getters.search.toLowerCase())   
     })
  },
   //users
   filteredViewOperations: function(state, getters){  
    return state.ViewOperations.filter((item)=>{
       return item.breadcrumbLabel.toLowerCase().match(getters.search.toLowerCase())  
     })
  },
  allusers: state => state.usersdata,
  allrecivers:state => state.recivers,
  secretuserlg: state => state.secretUserlg, 
  secretuser: state => state.secretUserpass, 
  //filters
  allPosition: state => state.position,
  allRoles: state => state.roles,
  allOrgs: state => state.orgs,

  //organizations
   allmainOrgs: state => state.mainOrgs,
   //dictionary
   alldictionary: state => state.dictionary,
   
   alldicbycode: state => state.dicByCode,
   allcontracttype: state => state.contractType,
   allcontractliability: state => state.contractLiability,
   alltown: state => state.town,
   allcountry: state => state.country,
   allregion: state => state.region,
   alltasktype: state => state.tasktype,
   //sellers
   allsellers: state => state.sellers,
    //feeTarifs
    allfeeTarifs: state => state.feeTarifs,
   //payment
   allpayments:state => state.payments,
   allpayplan:state => state.payplan,
   allViewOperations:state => state.ViewOperations,
   //files
   allDocuments:state => state.files,
   alluser_tasks: (state)  => state.user_tasks,
   alltasksCount: (state) => state.tasksCount,
   allorgUnitType: (state) => state.orgUnitType,
   allsuborgs:(state) => state.suborgs,
   allview_components:(state) => state.view_components,
   allcontractProcessState:(state) => state.contractProcessState,
   allsearchedContract:(state) => state.searchedContract,
};

const mutations = 
{ 
  translations:(state, translations) =>(state.translations= translations),
  setuser_tasks: (state, user_tasks) => (state.user_tasks = user_tasks),
  settasksCount: (state,  tasksCount) => (state.tasksCount = tasksCount),
  updateField,
  //formsContract
  formContract: (state, formContract) => (state.formContract = formContract),
  ViewOperations: (state, ViewOperations) => (state.ViewOperations = ViewOperations),
  viewComponents: (state, view_components) => (state.view_components = view_components),

   //login 
  retrieveToken(state, token) {state.token = token}, 
  retrieveUserinfo(state, i) {state.loginuser = JSON.parse(i)}, 
  destroyToken(state) {  state.token = null },
  destroyUserinfo(state) {  state.loginuser =  null },
    //main
  setNewagreement: (state, newagreement) => (state.newagreement = newagreement),
  setContracts: (state, searchedContract) => (state.searchedContract = searchedContract),
  oneContract: (state, oneCont) => (state.oneCont = oneCont),
  contractHistory: (state, historyCont) => (state.historyCont = historyCont),
 
  newContractdata: (state, contract) => state.newagreement.push(contract),
  updateCont(state, contract) {
    const index = state.newagreement.findIndex(item => item.id == contract.id)
    state.newagreement.splice(index, 1, contract)
  },
  
  updateSearch (state, search) { state.search = search },
  removeContract: (state, id) => (state.newagreement = state.newagreement.filter(agr => agr.id !== id)),
   //workers
  setWorkers: (state, workers) => (state.workers = workers),
  newWorkerdata: (state, worker) => state.workers.push(worker),
   //users
  setUsers: (state, usersdata) => (state.usersdata = usersdata),
  conRecivers:(state, recivers) => (state.recivers = recivers),
  newUserdata: (state, user) => state.usersdata.push(user), 
  removeUser: (state, id) => (state.userdata = state.userdata.filter(user => user.id !== id)),
  addRole: (state, role) => state.roles.push(role),
  updRole(state, role) {
    const index = state.roles.findIndex(item => item.id == role.id)
    state.roles.splice(index, 1, role)
  },
   //filters
  setPosition:(state, position) =>(state.position= position),
  addPosition: (state, pos) => state.position.push(pos), 
  setRoles:(state, roles) =>(state.roles= roles),
  setOrgs:(state, orgs) =>(state.orgs= orgs),
  setSubOrgs:(state, suborgs) =>(state.suborgs= suborgs),
   //organizations
  setmainOrgs:(state, mainOrgs) =>(state.mainOrgs= mainOrgs),
  //dictionary
  setDictionary: (state, dictionary) => (state.dictionary = dictionary),
  setContractType: (state, contractType) => (state.contractType = contractType),
  setContractProcessState: (state, contractProcessState) => (state.contractProcessState = contractProcessState),
  setContractLiability: (state, contractLiability) => (state.contractLiability = contractLiability),
  setCityTown: (state, town) => (state.town = town),
  setCountry: (state, country) => (state.country = country),
  setRegion: (state, region) => (state.region = region), 
  setOrganizationUnitType: (state, orgUnitType) => (state.orgUnitType = orgUnitType), 
  setTaskType: (state, tasktype) => (state.tasktype = tasktype),  
  setDictionaryByCode: (state, dicByCode) => (state.dicByCode = dicByCode),
  newDicItem: (state, newitem) => state.dicByCode.push(newitem),
  removeDicItem: (state, id) => (state.dicByCode = state.dicByCode.filter(item => item.id !== id)),
  //sellers
  setSellers: (state, sellers) => (state.sellers = sellers),
  newSellerdata: (state, seller) => state.sellers.push(seller), 
  removeSeller: (state, id) => (state.sellers = state.sellers.filter(seller => seller.id !== id)),
   //FeeTariffs
   setFeeTariffs: (state, feeTarifs) => (state.feeTarifs = feeTarifs),
   newFeeTariffdata: (state, fee) => state.feeTarifs.push(fee), 
   removeFeeTariff: (state, id) => (state.feeTarifs = state.feeTarifs.filter(fee => fee.id !== id)),
   //userRoles
   setUserRoles: (state, userRoles) => (state.userRoles = userRoles),
   addUserRole: (state, role) => state.userRoles.push(role), 
   removeUserRole: (state, id) => (state.userRoles = state.userRoles.filter(userRole => userRole.id !== id)),
  //paymant
  getPayment:(state, payments) => (state.payments = payments),
  crtPayment:(state, payment) => state.payments.push(payment), 
  updPayment(state, payment) {
    const index = state.payments.findIndex(item => item.id == payment.id)
    state.payments.splice(index, 1, payment)
  },
 //paymant_plan
 getPaymentPlan:(state, payplan) => (state.payplan = payplan),
 crtPaymentPlan:(state, payment) => state.payplan.push(payment), 
 updPaymentPlan(state, payment) {
   const index = state.payplan.findIndex(item => item.id == payment.id)
   state.payplan.splice(index, 1, payment)
 },
   //files
   setDocuments: (state, files) => (state.files = files),
    addDocument:(state, file) => state.files.push(file),
     removeDocument: (state, id) => (state.files = state.files.filter(file => file.id !== id)),
};

const actions = { 

       //view operations
       async userTasks(context, userId) {
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
        const response = await axios.get(`/Users/${userId}/UnCompletedTasks`);
        context.commit('setuser_tasks', response.data); 
        console.log(response.data);
        return response;
      }, 
 
       async unViewedTasksCount(context, userId) {
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
        const response = await axios.get(`/Users/${userId}/UnViewedTaskCount`); 
        context.commit('settasksCount', response.data); 
        console.log(response.data);
        return response;
      }, 

      async unComTasksView( context, obj)
      {   axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
      let response = await axios.post(`/Users/${obj.userId}/UnCompletedTasks/Views`, obj); 
   
       return response;
     },

     //view operations
  async getViewOperations(context) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/App/ViewOperations'); 
    context.commit('ViewOperations', response.data); 
    
    return response;
  },

  //form privilages
  async getForms(context) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/App/Forms'); 
    context.commit('formContract', response.data); 
    
    return response;
  }, 

  async getViewComponents(context,viewComponentId) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get(`/App/ViewComponents/${viewComponentId}`); 
    context.commit('viewComponents', response.data.commandList); 
    console.log(response.data.commandList)
    return response;
  }, 



//language
async getDefaultLanguage(context) {
  axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
  const response = await axios.get('/I18n/Languages'); 
  console.log( response.data);
  return response;
},

async getTranslationsById(context, languageId) {
  axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
  const response = await axios.get(`/I18n/Languages/${languageId}/Resources`); 
  console.log( response.data);
  return response;
},

  async getTranslationsByResourceType(context, obj) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get(`/I18n/Languages/${obj.languageId}/Resources/${obj.resourceType}`); 
    context.commit('translations', response.data);
    console.log( response.data);
    return response;
  },

  async changePassword(context, formData) { 
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    
    const response = await axios.put('/Auth/PermanentPasswords', formData ); 
    return response;
  },

  async resetPassword(context, formData) { 
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token 
    const response = await axios.post('/Auth/PasswordResets', formData ); 
    return response;
  },

  //login
  destroyToken(context) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    if (context.getters.loggedIn) {
      return new Promise((resolve, reject) => {
        axios.post('/Auth/WebLogouts')
          .then(response => {
            localStorage.removeItem('accessToken'); 
             localStorage.removeItem('userinfo');
            console.log(response.data.accessToken)
            context.commit('destroyToken');
            context.commit('destroyUserinfo');
            resolve(response); 
          })
          .catch(error => { 
            reject(error);
          })
      })
    }
  },

  retrieveToken(context, credentials) {
    return new Promise((resolve, reject) => {
      delete axios.defaults.headers.common["Authorization"];
      axios.defaults.headers.common['Accept-Language'] = i18n.locale;
      axios.post('/Auth/WebLogins', credentials )
          .then(response => { 
          const token = response.data.accessToken; 
        
          localStorage.setItem('accessToken', token);
          localStorage.setItem('userinfo', JSON.stringify(response.data));
          context.commit('retrieveToken', token); 
          context.commit('retrieveUserinfo', JSON.stringify(response.data)); 
           resolve(response); 
        })
        .catch(error => { 
          reject(error)
         })
      })    
  },

  permananentPass(context, credentials) {
    return new Promise((resolve, reject) => {
      axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token; 
      axios.post('/Auth/PermanentPasswords', credentials)
          .then(response => {   

           resolve(response);
        })
        .catch(error => { 
          reject(error)
         })
      })    
  },

  async checkSession( context)
  {   axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
  let response = await axios.post('/Auth/Web/AccessTokens/Checks');
  // return response;
   console.log(response.data);
 },

 //search menu
 
     async getSearchData(context, paramsData) 
    {
      axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token;
      var qs = require('qs');
      const response = await axios.get('/Contracts/Search', {
      params: { 
        registrationNumberSearchText: paramsData.registrationNumberSearchText,
        contractDateBegin: paramsData.contractDateBegin,
        contractDateEnd: paramsData.contractDateEnd,
        soldAssetAddressAddressSearchText : paramsData.soldAssetAddressAddressSearchText,
        contractNumberSearchText: paramsData.contractNumberSearchText,
        filterDebtorsOnly: paramsData.filterDebtorsOnly,
        soldAssetNameSearchText: paramsData.soldAssetNameSearchText,
 

        buyerNameSearchText: paramsData.buyerNameSearchText,
        buyerSurnameSearchText: paramsData.buyerSurnameSearchText,
        buyerPatronymicSearchText: paramsData.buyerPatronymicSearchText,
        processStatus : paramsData.processStatus 
      },
      paramsSerializer: params => { return qs.stringify(params, {arrayFormat: 'repeat'})}
    }); 
     console.log(response.data);
     context.commit('setContracts', response.data); 
    
    return response;
   } ,

  //contract

   async fetchContracts(context, actionQueryString) 
   {
     axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token; 
     const response = await axios.get(`/Contracts?${actionQueryString}`);  
    context.commit('setNewagreement', response.data); 
  } ,
   async addNewagreement( context, obj  )
   {   axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
   let response = await axios.post('/Contracts', obj);
   context.commit('newContractdata', response.data); 
    return response;
  },
  async getContract(context, contractId) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get(`/Contracts/${contractId}`);
    context.commit('oneContract', response.data); 
    return response;
  },

  async setContractViewedByUser(context, contractId) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.post(`/Contracts/${contractId}/Views`);  
    return response;
  },

  async getContractReceivers(context, contractId) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get(`/Contracts/${contractId}/Receivers`);
      context.commit('conRecivers', response.data); 
    return response;
  },

  async updateContract(context, formData) {
    
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.put(`/Contracts/${formData.id}`, formData );
    context.commit('updateCont', response.data);
    return response;
  },
   
  async getContractHistory(context, contractId) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get(`/Contracts/${contractId}/ActivityHistories`);
    context.commit('contractHistory', response.data);
    console.log( response.data);
    return response;
  },

  async deleteContract(context, obj ) 
  { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token;
   const response = await axios.delete(`/Contracts/${obj.contractId}` , {params: { switchOnlyStatus : obj.switchOnlyStatus }} );
    context.commit('removeContract', obj.contractId);
    return response;
  },

  //files

  async fetchContractDocuments(context, contractId) 
  { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
   const response = await axios.get(`/Contracts/${contractId}/Documents`); 
   context.commit('setDocuments', response.data);  
   return response;
  },
  
  async addContractDocuments(context, obj) {
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    axios.defaults.headers.common['Content-Type'] =  'multipart/form-data'
    const response = await  axios.post(`/Contracts/${obj.contractId}/Documents`, obj);
    context.commit('addDocument', response.data); 
    return response;
  },

   async deleteDocument(context ,obj ) 
  { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token;
    await axios.delete(`/Contracts/${obj.contractId}/Documents/${obj.documentId}`);
    context.commit('removeDocument', obj.documentId);
    
  },

   async downloadContractDocument(context, obj) 
  { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
   const response = await axios.get(`/Contracts/${obj.contractId}/Documents/${obj.documentId}/File`); 
//   context.commit('setDocuments', response.data);  
console.log(response.data);
   return response;
  },

   // dictionary
   async fetchDictionary(context) 
   { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries'); 
    context.commit('setDictionary', response.data);  
    return response;
   },

   async fetchDictionaryByCode(context, dictionaryCode ){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get(`/Dictionaries/${dictionaryCode}`);
    context.commit('setDictionaryByCode', response.data.itemList);  
    return response;
   },

   async addNewDicCodeItem( context  , formData )
   {   axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
   let response = await axios.post(`/Dictionaries/${formData.dictionaryCode}/Items`, formData);
    context.commit('newDicItem', response.data); 
    return response;
  },

  async deleteDictionaryItem(context ,obj ) 
  { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token;
    await axios.delete(`/Dictionaries/${obj.dictionaryCode}/Items/${obj.dictionaryItemId}`);
    context.commit('removeDicItem', obj.dictionaryItemId);
    
  },

   async fetchContType(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/ContractType');
    context.commit('setContractType',  response.data.itemList);  
    return response;
   },

   async fetchContractProcessState(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/ContractProcessState');
    context.commit('setContractProcessState',  response.data.itemList);  
    return response;
   },
   
   async fetchContractLiability(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/ContractLiability');
    context.commit('setContractLiability',  response.data.itemList);  
    return response;
   },
   async fetchCity(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/CityTown');
    context.commit('setCityTown', response.data.itemList);
    return response;
   },
   async fetchCountry(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/Country');
    context.commit('setCountry', response.data.itemList);
    return response;
   },
   async fetchRegion(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/Region');
    context.commit('setRegion', response.data.itemList);
    return response;
   }, 

   async fetchOrganizationUnitType(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/OrganizationUnitType');
    context.commit('setOrganizationUnitType', response.data.itemList);
    return response;
   }, 

   async addOrganizationUnit(context,organizationUnitCreateRequest){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.post('/OrganizationUnits', organizationUnitCreateRequest);
    // context.commit('addOrgUnit', response.data);
    return response;
   }, 

    
   async fetchContractTaskType(context){
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Dictionaries/ContractTaskType');
    context.commit('setTaskType', response.data.itemList);
    return response;
   },
   //seller
   async fetchSellers(context) 
   { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
    const response = await axios.get('/Sellers'); 
    context.commit('setSellers', response.data);  
    return response;
   },

   async addSeller( context, obj )
   {   axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
   let response = await axios.post('/Sellers', obj );
   context.commit('newSellerdata', response.data); 
    return response;
  },

  async deleteSeller(context,sellerId ) 
  { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token;
    await axios.delete(`/Sellers/${sellerId}`);
    context.commit('removeSeller', sellerId);
  },
 //feeTarifs
 async fetchFeeTariffs(context) 
 { axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
  const response = await axios.get('/FeeTariffs'); 
  context.commit('setFeeTariffs', response.data);  
  return response;
 },

 async addFeeTariffs( context, obj )
 {   axios.defaults.headers.common['Authorization'] = 'Bearer ' + context.state.token
